def flat_array(df):
    arr_list = [c[0] for c in df.dtypes if c[1][:5] == 'array']

    while len(arr_list) > 0:
        for c in arr_list:
            df = df.withColumn(c,explode_outer(c))
        df = struct_fil(df)
        arr_list = [c[0] for c in df.dtypes if c[1][:5] == 'array']

