def final_list(df):
    arra_c = [c[0] for c in df.dtypes if c[1][:5] == 'array']

    while len(arra_c) > 0:
        for c in arra_c:
            df = df.withColumn(c,explode_outer(c))

        df = extract_struct(df)
        arra_c = [c[0] for c in df.dtypes if c[1][:5] == 'array']

    return df

