import xml.etree.ElementTree as ET
import pandas as pd

data = ET.parse('/content/orchestra.xml')
root = data.getroot()
#root

def xml_parse(element):
  item = {}
  for child in element:
    if child.tag not in item:
      item[child.tag]=[]
    if list(child):
      item[child.tag].append(xml_parse(child))
    else:
      item[child.tag]=child.text



  return item

data_xml = [xml_parse(program) for program in root.findall('.//program')]

df = pd.json_normalize(data_xml)
